import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

function PokerRoom() {
    const { roomName } = useParams();
    const [username, setUsername] = useState('');
    const [users, setUsers] = useState([]);
    const [sequence, setSequence] = useState([]);
    const [votes, setVotes] = useState({});
    const [reveal, setReveal] = useState(false);

    useEffect(() => {
        const name = prompt('Digite seu nome');
        setUsername(name);
        socket.emit('joinRoom', { roomName, username: name });

        socket.on('roomData', (data) => {
            setUsers(data.users);
            setSequence(data.sequence);
        });

        socket.on('votes', (votes) => {
            setVotes(votes);
        });

        socket.on('revealVotes', () => {
            setReveal(true);
        });

        return () => {
            socket.disconnect();
        };
    }, [roomName]);

    const handleVote = (value) => {
        socket.emit('vote', { roomName, username, value });
    };

    const handleReveal = () => {
        socket.emit('reveal', roomName);
    };

    return (
        <div>
            <h1>Sala: {roomName}</h1>
            <h2>Participantes:</h2>
            <ul>
                {users.map((u) => (
                    <li key={u}>{u}: {reveal ? votes[u] : 'Votando...'}</li>
                ))}
            </ul>

            <h2>Cartas:</h2>
            {sequence.map((card, idx) => (
                <button key={idx} onClick={() => handleVote(card)}>{card}</button>
            ))}

            <div>
                <button onClick={handleReveal}>Revelar Votos</button>
            </div>
        </div>
    );
}

export default PokerRoom;
